// script for full height sidebar
$(document).ready(function(){
	$('.menu-toggler').click(function(){
		$(this).toggleClass("active");
		$(".navbar-menu").toggleClass("active");
	});
});

// Script For full width sidebar
var MenuItems = document.getElementById("MenuItems");
	MenuItems.style.maxHeight = "0px";
								
	function menutoggle(){
	if(MenuItems.style.maxHeight == "0px"){
		MenuItems.style.maxHeight = "200px";
	}
	else{
		MenuItems.style.maxHeight = "0px";
	}
}

// script for spaceui-form-1 validation btn login

var fields = document.querySelectorAll(".textb input");
var btn = document.querySelector(".p-form-1-btn");
function check(){
	if(fields[0].value !="" && fields[1].value !="")
	btn.disabled = false;
else
	btn.disabled = true;
}

fields[0].addEventListener("keyup",check);
fields[1].addEventListener("keyup",check);

document.querySelector(".show-password").addEventListener("click", function(){
	if(this.classList[2] == "fa-eye-slash"){
		this.classList.remove("fa-eye-slash");
		this.classList.add("fa-eye");
		fields[1].type = "text";
	}else{
		this.classList.remove("fa-eye");
		this.classList.add("fa-eye-slash");
		fields[1].type = "password";
	}
});

//SpaceUI Modal script Get the modal
var modal = document.getElementById("myModal");
				
// Get the button that opens the modal
var btn = document.getElementById("myModalBtn");
				
// Get the <span> element that closes the modal
var span = document.getElementsByClassName("close")[0];
				
// When the user clicks the button, open the modal 
btn.onclick = function() {
	modal.style.display = "block";
}
				
// When the user clicks on <span> (x), close the modal
span.onclick = function() {
	modal.style.display = "none";
}
				
// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
	if (event.target == modal) {
		modal.style.display = "none";
	}
}